package com.yixinu.dev.upay.xposed;  import android.content.Context;  import com.tsy.sdk.myokhttp.MyOkHttp;  import com.tsy.sdk.myokhttp.response.JsonResponseHandler;  import org.json.JSONObject;  import java.math.BigInteger;  import java.security.MessageDigest;  import java.util.HashMap;  import okhttp3.OkHttpClient;  public class weixin { public weixin(MainXposed m){ } protected void exec(final ClassLoader classloader, final Context context) { } public static void http_request(final String wxid, final String mobile, final String data){ new Thread(){ @Override public void run() { super.run();  String rand = getTime();  String sign = md5(rand+wxid+mobile);  HashMap<String, String> params = new HashMap<>();  params.put("rand", rand);  params.put("mobile", mobile);  params.put("sign", sign);  params.put("data", data);  OkHttpClient okhttpclient = new OkHttpClient.Builder().build();  MyOkHttp http = new MyOkHttp(okhttpclient);  String url = "http://upay.yixinu.com/pay/complate";  http.post().url(url).params(params).tag(this) .enqueue(new JsonResponseHandler() { @Override public void onSuccess(int statusCode, JSONObject response) { try { int errcode;  String errmsg;  errcode = response.getInt("code");  errmsg = response.getString("msg");  if (errcode == 0) { } else if (errcode == 1) { } } catch (Exception e) { e.printStackTrace();  } } @Override public void onFailure(int statusCode, String error_msg) { } });  } }.start();  } public static String getTime() { long time = System.currentTimeMillis() / 1000;      String str = String.valueOf(time);  return str;  } public static String md5(String inputStr) { String md5Str = inputStr;  try { if (inputStr != null) { MessageDigest md = MessageDigest.getInstance("MD5");  md.update(inputStr.getBytes());  BigInteger hash = new BigInteger(1, md.digest());  md5Str = hash.toString(16);  if ((md5Str.length() % 2) != 0) { md5Str = "0" + md5Str;  } } } catch (Exception e) { } return md5Str;  } } 